properties.sql-pre-query:
DROP TABLE IF EXISTS ${schema}.glue_tbls_with_total_col;
DROP TABLE IF EXISTS ${schema}.parsed_fields;
DROP TABLE IF EXISTS ${schema}.columns_with_rules;



create table ${schema}.parsed_fields(column_name varchar(20), data_type varchar(20), 
nullable boolean, default_value varchar(20), valid_values varchar(200), table_name varchar(20),total_columns int);

create table ${schema}.glue_tbls_with_total_col(table_name varchar(20), total_columns int);

properties.SQL select query:
SELECT 
    (SELECT SUM(total_columns) FROM ${schema}.glue_tbls_with_total_col) - COUNT(*) AS col_remains
FROM ${schema}.parsed_fields;
