CREATE SCHEMA IF NOT EXISTS ${schema}; DROP TABLE IF EXISTS dq_checks.dq_rules; DROP TABLE IF EXISTS dq_checks.table_with_total_count; DROP TABLE IF EXISTS dq_checks.merged_result_tbl; DROP TABLE IF EXISTS ${schema}.pass_rec_per_table;
CREATE TABLE IF NOT EXISTS dq_checks.dq_rules ( rule_id BIGINT, rule_name VARCHAR(200), connection_id INT, table_name VARCHAR(50), validation_type VARCHAR(50), validation_grp VARCHAR(50), column_name VARCHAR(50), validation_rule VARCHAR(50), condition VARCHAR(100), column_description VARCHAR(200), rule_description VARCHAR(200) );
CREATE TABLE IF NOT EXISTS dq_checks.table_with_total_count ( table_name VARCHAR(50), row_count INTEGER );
CREATE TABLE IF NOT EXISTS dq_checks.merged_result_tbl ( run_date DATE, table_name VARCHAR(50), concatenated_pk_columns VARCHAR(200), rule_id VARCHAR(50), rule_name VARCHAR(200), validation_grp VARCHAR(50), validation_type VARCHAR(50), total_record_in_table INTEGER, run_id BIGINT, total_rules INTEGER, query_generation_status VARCHAR(100), validation_rule VARCHAR(50) );
CREATE TABLE IF NOT EXISTS dq_checks.dqstat(run_date varchar(20), run_id BIGINT, table_name varchar(20), rule_id integer, fail_record_count integer, total_record_count bigint);
CREATE TABLE IF NOT EXISTS dq_checks.dqsummary( run_date varchar(20), run_id BIGINT, validation_group varchar(20), fail_record_count integer, total_record_count bigint);
CREATE TABLE IF NOT EXISTS dq_checks.dqscore( run_date varchar(20), run_id BIGINT, table_name varchar(20), fail_record_count integer, total_record_count bigint);
CREATE TABLE IF NOT EXISTS ${schema}.pass_rec_per_table( table_name varchar(50), total_record_in_table integer, validation_grp varchar(50), pass_cnt_for_completeness integer );

-- Step 1: Create the enriched base table excluding 'no_column' and tagging PK failures DROP TABLE IF EXISTS dq_checks.merge_tbl_with_filtered_column;
CREATE TABLE dq_checks.merge_tbl_with_filtered_column AS SELECT m.table_name, m.concatenated_pk_columns, m.rule_id, m.validation_grp, m.validation_rule, CASE WHEN pk_chk.concatenated_pk_columns IS NOT NULL THEN pk_chk.concatenated_pk_columns ELSE NULL END AS pk_available_in_fail_pk FROM dq_checks.merged_result_tbl m LEFT JOIN ( SELECT DISTINCT table_name, concatenated_pk_columns FROM dq_checks.merged_result_tbl WHERE validation_rule = 'primary_key_check' ) pk_chk ON m.table_name = pk_chk.table_name AND m.concatenated_pk_columns = pk_chk.concatenated_pk_columns AND m.validation_rule != 'primary_key_check' WHERE m.concatenated_pk_columns != 'no_column';
-- step 2-- Table-level grouping DROP TABLE IF EXISTS dq_checks.fail_record_grouped_by_table; CREATE TABLE dq_checks.fail_record_grouped_by_table AS SELECT table_name, COUNT(*) AS final_failed_record_count FROM ( SELECT DISTINCT table_name, concatenated_pk_columns FROM dq_checks.merge_tbl_with_filtered_column WHERE pk_available_in_fail_pk IS NULL AND validation_rule != 'primary_key_check'
UNION ALL
SELECT table_name, concatenated_pk_columns FROM dq_checks.merge_tbl_with_filtered_column WHERE validation_rule = 'primary_key_check' ) AS uni GROUP BY table_name;
DROP TABLE IF EXISTS dq_checks.merge_tbl_with_filtered_column;

select * from ${schema}.fail_record_grouped_by_table
union all
select * from ${schema}.fail_record_grouped_by_validation;
